DROP TABLE MOVIE;

create table MOVIE
(
	ID_IMDB VARCHAR(20) not null primary key,
	TITLE VARCHAR(100) not null,
	RANKING INTEGER,
	RATING FLOAT,
	POSTER_URL VARCHAR(250),
	RELEASE INTEGER
);
